function interpolate(str, params = {}) {
  return str.replace(/{{\s*([^}]+)\s*}}/g, (_, key) => {
    return params[key] != null ? String(params[key]) : ''
  })
}

export function t(key, params = {}, lang = 'id') {
  const dict = translations[lang] || translations.id
  const val = dict[key] || translations.id[key] || key
  return interpolate(val, params)
}

export const availableLanguages = ['id', 'en', 'ar', 'zh']

export default {
  translations,
  t,
  availableLanguages
}
